package testPackage;

import org.testng.annotations.Test;
import org.openqa.selenium.WebDriver;
import org.openqa.selenium.chrome.ChromeDriver;
import org.testng.Assert;

public class SeleniumTests {
    @Test
    public void sampleSeleniumTest() {
        System.out.println("This is a sample Selenium test.");
    }

    @Test
    public void basicDuckDuckGoTest() {
        // Set up ChromeDriver (assumes chromedriver is in PATH)
        WebDriver driver = new ChromeDriver();
        try {
            driver.get("https://duckduckgo.com/");
            String title = driver.getTitle();
            Assert.assertEquals(title, "DuckDuckGo - Protection. Privacy. Peace of mind.", "Page title should be 'DuckDuckGo - Protection. Privacy. Peace of mind.'");
        } finally {
            driver.quit();
        }
    }
}

